package PatternQuestions;

public class APattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int aniket,ashutosh,lokesh;
		for(aniket=1;aniket<=5;aniket++) 
		{
		
		for(ashutosh=1;ashutosh>1;ashutosh--) {
			System.out.println(" ");
		}
		for(lokesh=1;lokesh<=aniket;lokesh++) {
			System.out.println("* ");
		}
    System.out.println("\n");
	}

}
}
