package javaPrograms;

public class Datatypeinjava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		
		byte a = 20; //1 byte
		
		int b = 30;  //4 byte
		
		short c = 50; //2 byte 
		
		double d = 10; // 8 byte
		
		long l = 10000l; //8 byte
		
		char ch = 'L'; //2 byte It's single data type
		
		float f1 = 555.5f; // 32 bit
		
		boolean value = true; // size can't be defined (only output True/false)
		
				
		System.out.println(value);
	}

}
